import React, { useEffect, useRef, useState } from 'react';
import MyImage from '../Images/missing.png';

function Home(props){
    // Create your own Mock API: https://mockapi.io/
    // const url = `https://5e9623dc5b19f10016b5e31f.mockapi.io/api/v1/products?page=1&limit=10`
    // let products = useAxiosGet(url)

    const { classes } = props;
    const inputFile = useRef(null);

    // Component States
    const [dbfile, setdbfile] = useState(null);

    const handleImageUploadClick = () => {
        inputFile.current.click();
    }

    const handleOnChange = (event) => {
        console.log(event.target.files);
        setdbfile(URL.createObjectURL(event.target.files[0]));

        var formData = new FormData();
        formData.append("file", event.target.files[0]);
        // DataService.Predict(formData)
        //     .then(function (response) {
        //         console.log(response.data);
        //         setPrediction(response.data);
        //     })
    }

    let content = <div>
        <div className="mb-2 p-3">
            This is our project for missing value imputation, upload a database to get started!
        </div>
        <img src={MyImage} alt="horse" />
        <div className={classes.dropzone}>
        <input
            type="file"
            accept="image/*"
            on
            autocomplete="off"
            tabindex="-1"
            className={classes.fileInput}
            ref={inputFile}
            onChange={(event) => handleOnChange(event)}
            />
        </div>
    </div>


    return (
        <div className="container mx-auto">
            <h1 className="font-bold text-2xl mb-3 text-center">
                Database Missing Value Imputation
            </h1>
            <div className="md:flex flex-wrap md:-mx-3">
                { content } 
            </div>
        </div>
    )
}

export default Home
